package omar.com;import java.util.Scanner;public class Main {    public static void main(String[] args) {        System.out.println("[-Zaad-]");        while (true) {            System.out.println("1.Itus Haraaga\n2.Lagac Dir");            int answer = readInt();            switch (answer) {                case 1:                    showBalance();                    break;                case 2:                    sendMoney();                default:                    System.out.println("Tiro Qalad Ah Ayad Galisay Fadlan iska Hubi..");            }        }    }    private static void showBalance(){        System.out.println("Haraaga Waa $" + Account.myAccount.getBalance());    }    private  static void sendMoney(){        System.out.println("Lambarka ad u direysid Gali:");        String number  = readLine();        Account account =Account.getAccount(number);        if (account == null){            System.out.println("Lambarkaga Ad Galisay Majiro\nFadlan iska Hubi");            return;        }        System.out.println("Lacagta gali");        int amount = readInt();        System.out.println("Ma hubtaa inad $" +amount + " u dirtid " + account.getName());        String answer = readLine();        if (!answer.equalsIgnoreCase("haa"))            return;            Account.myAccount.sendMoney(amount,account);    }    private  static  String readLine(){        Scanner input = new Scanner(System.in);        return input.nextLine();    }    private static int readInt(){        Scanner input = new Scanner(System.in);        return input.nextInt();    }}